mapper.update:
update ht_enterprise_info
set enterprise_name = #{enterpriseName,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  update_ = #{update,jdbcType=TIMESTAMP}
where enterprise_id = #{enterpriseId,jdbcType=INTEGER}

mapper.select:
select

from ht_enterprise_info a ,ht_enterprise_user_rela b
on a.enterprise_id=b.enterprise_id
where b.user_id = #{userId,jdbcType=INTEGER}

  and a.enterprise_name like concat('%',#{enterpriseName,jdbcType=VARCHAR},'%')

;

select
count(*)
from ht_enterprise_info a
where enterprise_name = #{enterpriseName,jdbcType=VARCHAR}
;
